var ww=window.innerWidth,wh=window.innerHeight;window.onresize=function(e){ww=window.innerWidth,wh=window.innerHeight};var mx,my,midX,midY,posX,posY,mt,ms,mi,o;document.addEventListener("mousemove",function(e){var m=document.getElementById("hidPosX"),t=document.getElementById("hidPosY");mx=e.clientX,my=e.clientY,midY=wh/2,posX=mx<(midX=ww/2)?-(midX-mx):mx-midX,posY=my<midY?-(midY-my):my-midY,m.value=posX,t.value=posY;var n=1-Math.abs(posX)/midX;o=n+.4,mt=document.getElementById("mt"),ms=document.getElementById("ms"),mi=document.getElementById("mi"),mt.style.transform="translate("+-.032*m.value+"px, "+-.016*t.value+"px)",ms.style.transform="translate("+-.064*m.value+"px, "+-.032*t.value+"px)",ms.style.textShadow="0px 5px 18px rgba(150, 150, 150, "+.45*o+")",mi.style.transform="translate("+.032*m.value+"px,"+.016*t.value+"px)"});